import React from 'react';
import { Input } from '../../form/Input';

export const Step1 = () => {
  return (
    <form>
      <h1>Personal Info</h1>
      <span>Please provide your name ,email adress, and phone number</span>
      <Input text="Name" name="name" type="text" />
      {/* <span>the field is required</span> */}
      <Input text="Email Adress" name="email" type="mail" />
      <Input text="Phone Number" name="phone" type="text" />
    </form>
  );
};
